mapper.select:


     and student_id = #{userId}
     and academic_year = #{academicYear}
     and semester = #{semester}
     and class_id = #{classId}
     and status = #{status}
     and moral_score = #{moralScore}
     and intellectual_score = #{intellectualScore}
     and physical_score = #{physicalScore}
     and aesthetic_score = #{aestheticScore}
     and labor_score = #{laborScore}
     and total_score = #{totalScore}
     and submit_time = #{submitTime}
     and audit_time = #{auditTime}
     and audit_by = #{auditBy}
     and audit_comment = #{auditComment}

order by create_time desc

select count(*) from evaluation_submission

     and student_id = #{userId}
     and academic_year = #{academicYear}
     and semester = #{semester}
     and class_id = #{classId}
     and status = #{status}

select 
    status,
    count(*) as count
from evaluation_submission

     and student_id = #{userId}
     and academic_year = #{academicYear}
     and semester = #{semester}
     and class_id = #{classId}

group by status

select 
    avg(moral_score) as avgMoralScore,
    avg(intellectual_score) as avgIntellectualScore,
    avg(physical_score) as avgPhysicalScore,
    avg(aesthetic_score) as avgAestheticScore,
    avg(labor_score) as avgLaborScore,
    avg(total_score) as avgTotalScore
from evaluation_submission

     and student_id = #{userId}
     and academic_year = #{academicYear}
     and semester = #{semester}
     and class_id = #{classId}
     and status = #{status}
